INSERT INTO centres (name, description, address, sort_order, active) VALUES
  ('Centre FCRA Andakana', 'Le Centre FCRA Andakana est situé dans la commune rurale d’Andakana. Il représente aujourd’hui le plus grand complexe du réseau FCRA à Madagascar.Doté d’infrastructures modernes, le centre d’Andakana symbolise l’ambition du FCRA : offrir à la jeunesse malgache un accès équitable à une éducation de qualité, dans un cadre propice à l’épanouissement personnel et collectif.', 'Andakana', 1, true),
  ('Centres FCRA Manakara', 'Créés il y a bientôt trois ans, les centres FCRA de Manakara et Sakoana incarnent la volonté du FCRA d’élargir son action éducative dans des zones stratégiques, souvent négligées, en accompagnant les jeunes vers un avenir meilleur.', 'Manakara', 2, true),
  ('Centres FCRA Sakoana', 'Créés il y a bientôt trois ans, les centres FCRA de Sakoana incarnent la volonté du FCRA d’élargir son action éducative dans des zones stratégiques, souvent négligées, en accompagnant les jeunes vers un avenir meilleur.', 'Sakoana', 3, true),
  ('Centre FCRA Antohomadinika', 'Le Centre FCRA Antaniavo est situé à Antohomadinika, et constitue le siège social de l’ensemble des structures FCRA. Il joue un rôle central dans la coordination des actions sociales, éducatives et humanitaires menées à travers le pays.', 'III D 36 Ter , Antaniavo Antohomadinika Antananarivo', 4, true);